        LOGGER.debug("Updating Insurancecoveragetype with information: {}", insurancecoveragetype);
        this.wmGenericDao.update(insurancecoveragetype);

        Integer insurancecoveragetypeId = insurancecoveragetype.getPk();

        return this.wmGenericDao.findById(insurancecoveragetypeId);
