package com.lsuc.lsuc.service;

/*This is a Studio Managed File. DO NOT EDIT THIS FILE. Your changes may be reverted by Studio.*/

import java.util.HashMap;
import java.util.Map;

import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.beans.factory.annotation.Qualifier;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.Pageable;
import org.springframework.stereotype.Service;
import org.springframework.transaction.annotation.Transactional;

import com.wavemaker.runtime.data.dao.WMGenericDao;
import com.wavemaker.runtime.data.exception.EntityNotFoundException;
import com.wavemaker.runtime.data.export.ExportType;
import com.wavemaker.runtime.data.expression.QueryFilter;
import com.wavemaker.runtime.data.model.AggregationInfo;
import com.wavemaker.runtime.file.model.Downloadable;

import com.lsuc.lsuc.Licencetype;
import com.lsuc.lsuc.Licensee;


/**
 * ServiceImpl object for domain model class Licencetype.
 *
 * @see Licencetype
 */
@Service("LSUC.LicencetypeService")
public class LicencetypeServiceImpl implements LicencetypeService {

    private static final Logger LOGGER = LoggerFactory.getLogger(LicencetypeServiceImpl.class);

    @Autowired
	@Qualifier("LSUC.LicenseeService")
	private LicenseeService licenseeService;

    @Autowired
    @Qualifier("LSUC.LicencetypeDao")
    private WMGenericDao<Licencetype, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<Licencetype, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "LSUCTransactionManager")
    @Override
	public Licencetype create(Licencetype licencetype) {
        LOGGER.debug("Creating a new Licencetype with information: {}", licencetype);
        Licencetype licencetypeCreated = this.wmGenericDao.create(licencetype);
        if(licencetypeCreated.getLicensees() != null) {
            for(Licensee licensee : licencetypeCreated.getLicensees()) {
                licensee.setLicencetype(licencetypeCreated);
                LOGGER.debug("Creating a new child Licensee with information: {}", licensee);
                licenseeService.create(licensee);
            }
        }
        return licencetypeCreated;
    }

	@Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public Licencetype getById(Integer licencetypeId) throws EntityNotFoundException {
        LOGGER.debug("Finding Licencetype by id: {}", licencetypeId);
        Licencetype licencetype = this.wmGenericDao.findById(licencetypeId);
        if (licencetype == null){
            LOGGER.debug("No Licencetype found with id: {}", licencetypeId);
            throw new EntityNotFoundException(String.valueOf(licencetypeId));
        }
        return licencetype;
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public Licencetype findById(Integer licencetypeId) {
        LOGGER.debug("Finding Licencetype by id: {}", licencetypeId);
        return this.wmGenericDao.findById(licencetypeId);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Licencetype getByCode(String code) {
        Map<String, Object> codeMap = new HashMap<>();
        codeMap.put("code", code);

        LOGGER.debug("Finding Licencetype by unique keys: {}", codeMap);
        Licencetype licencetype = this.wmGenericDao.findByUniqueKey(codeMap);

        if (licencetype == null){
            LOGGER.debug("No Licencetype found with given unique key values: {}", codeMap);
            throw new EntityNotFoundException(String.valueOf(codeMap));
        }

        return licencetype;
    }

	@Transactional(rollbackFor = EntityNotFoundException.class, value = "LSUCTransactionManager")
	@Override
	public Licencetype update(Licencetype licencetype) throws EntityNotFoundException {
        LOGGER.debug("Updating Licencetype with information: {}", licencetype);
        this.wmGenericDao.update(licencetype);

        Integer licencetypeId = licencetype.getPk();

        return this.wmGenericDao.findById(licencetypeId);
    }

    @Transactional(value = "LSUCTransactionManager")
	@Override
	public Licencetype delete(Integer licencetypeId) throws EntityNotFoundException {
        LOGGER.debug("Deleting Licencetype with id: {}", licencetypeId);
        Licencetype deleted = this.wmGenericDao.findById(licencetypeId);
        if (deleted == null) {
            LOGGER.debug("No Licencetype found with id: {}", licencetypeId);
            throw new EntityNotFoundException(String.valueOf(licencetypeId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public Page<Licencetype> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all Licencetypes");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Page<Licencetype> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all Licencetypes");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service LSUC for table Licencetype to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Page<Licensee> findAssociatedLicensees(Integer pk, Pageable pageable) {
        LOGGER.debug("Fetching all associated licensees");

        StringBuilder queryBuilder = new StringBuilder();
        queryBuilder.append("licencetype.pk = '" + pk + "'");

        return licenseeService.findAll(queryBuilder.toString(), pageable);
    }

    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service LicenseeService instance
	 */
	protected void setLicenseeService(LicenseeService service) {
        this.licenseeService = service;
    }

}

