	public Othernametype create(Othernametype othernametype) {
        LOGGER.debug("Creating a new Othernametype with information: {}", othernametype);
        Othernametype othernametypeCreated = this.wmGenericDao.create(othernametype);
        if(othernametypeCreated.getPersonothernames() != null) {
            for(Personothername personothername : othernametypeCreated.getPersonothernames()) {
                personothername.setOthernametype(othernametypeCreated);
                LOGGER.debug("Creating a new child Personothername with information: {}", personothername);
                personothernameService.create(personothername);
            }
        }
        return othernametypeCreated;
    }

