import com.lsuc.lsuc.Personphonecontact;
import com.lsuc.lsuc.Phonepurpose;


/**
 * ServiceImpl object for domain model class Phonepurpose.
 *
 * @see Phonepurpose
 */
@Service("LSUC.PhonepurposeService")
public class PhonepurposeServiceImpl implements PhonepurposeService {

    private static final Logger LOGGER = LoggerFactory.getLogger(PhonepurposeServiceImpl.class);

    @Autowired
	@Qualifier("LSUC.PersonphonecontactService")
	private PersonphonecontactService personphonecontactService;

    @Autowired
	@Qualifier("LSUC.BusinessphonecontactService")
	private BusinessphonecontactService businessphonecontactService;

    @Autowired
    @Qualifier("LSUC.PhonepurposeDao")
    private WMGenericDao<Phonepurpose, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<Phonepurpose, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "LSUCTransactionManager")
    @Override
	public Phonepurpose create(Phonepurpose phonepurpose) {
        LOGGER.debug("Creating a new Phonepurpose with information: {}", phonepurpose);
        Phonepurpose phonepurposeCreated = this.wmGenericDao.create(phonepurpose);
        if(phonepurposeCreated.getPersonphonecontacts() != null) {
            for(Personphonecontact personphonecontact : phonepurposeCreated.getPersonphonecontacts()) {
                personphonecontact.setPhonepurpose(phonepurposeCreated);
                LOGGER.debug("Creating a new child Personphonecontact with information: {}", personphonecontact);
                personphonecontactService.create(personphonecontact);
            }
        }

        if(phonepurposeCreated.getBusinessphonecontacts() != null) {
            for(Businessphonecontact businessphonecontact : phonepurposeCreated.getBusinessphonecontacts()) {
                businessphonecontact.setPhonepurpose(phonepurposeCreated);
                LOGGER.debug("Creating a new child Businessphonecontact with information: {}", businessphonecontact);
                businessphonecontactService.create(businessphonecontact);
            }
        }
        return phonepurposeCreated;
    }

	@Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public Phonepurpose getById(Integer phonepurposeId) throws EntityNotFoundException {
        LOGGER.debug("Finding Phonepurpose by id: {}", phonepurposeId);
        Phonepurpose phonepurpose = this.wmGenericDao.findById(phonepurposeId);
        if (phonepurpose == null){
            LOGGER.debug("No Phonepurpose found with id: {}", phonepurposeId);
            throw new EntityNotFoundException(String.valueOf(phonepurposeId));
        }
        return phonepurpose;
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public Phonepurpose findById(Integer phonepurposeId) {
        LOGGER.debug("Finding Phonepurpose by id: {}", phonepurposeId);
        return this.wmGenericDao.findById(phonepurposeId);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Phonepurpose getByCode(String code) {
        Map<String, Object> codeMap = new HashMap<>();
        codeMap.put("code", code);

        LOGGER.debug("Finding Phonepurpose by unique keys: {}", codeMap);
        Phonepurpose phonepurpose = this.wmGenericDao.findByUniqueKey(codeMap);

        if (phonepurpose == null){
            LOGGER.debug("No Phonepurpose found with given unique key values: {}", codeMap);
            throw new EntityNotFoundException(String.valueOf(codeMap));
        }

        return phonepurpose;
    }

	@Transactional(rollbackFor = EntityNotFoundException.class, value = "LSUCTransactionManager")
	@Override
	public Phonepurpose update(Phonepurpose phonepurpose) throws EntityNotFoundException {
        LOGGER.debug("Updating Phonepurpose with information: {}", phonepurpose);
        this.wmGenericDao.update(phonepurpose);

        Integer phonepurposeId = phonepurpose.getPk();

        return this.wmGenericDao.findById(phonepurposeId);
    }

    @Transactional(value = "LSUCTransactionManager")
	@Override
	public Phonepurpose delete(Integer phonepurposeId) throws EntityNotFoundException {
        LOGGER.debug("Deleting Phonepurpose with id: {}", phonepurposeId);
        Phonepurpose deleted = this.wmGenericDao.findById(phonepurposeId);
        if (deleted == null) {
            LOGGER.debug("No Phonepurpose found with id: {}", phonepurposeId);
            throw new EntityNotFoundException(String.valueOf(phonepurposeId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public Page<Phonepurpose> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all Phonepurposes");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Page<Phonepurpose> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all Phonepurposes");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service LSUC for table Phonepurpose to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Page<Personphonecontact> findAssociatedPersonphonecontacts(Integer pk, Pageable pageable) {
        LOGGER.debug("Fetching all associated personphonecontacts");

        StringBuilder queryBuilder = new StringBuilder();
        queryBuilder.append("phonepurpose.pk = '" + pk + "'");

        return personphonecontactService.findAll(queryBuilder.toString(), pageable);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Page<Businessphonecontact> findAssociatedBusinessphonecontacts(Integer pk, Pageable pageable) {
        LOGGER.debug("Fetching all associated businessphonecontacts");

        StringBuilder queryBuilder = new StringBuilder();
        queryBuilder.append("phonepurpose.pk = '" + pk + "'");

        return businessphonecontactService.findAll(queryBuilder.toString(), pageable);
    }

    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service PersonphonecontactService instance
	 */
	protected void setPersonphonecontactService(PersonphonecontactService service) {
        this.personphonecontactService = service;
    }

    /**
	 * This setter method should only be used by unit tests
	 *
	 * @param service BusinessphonecontactService instance
	 */
	protected void setBusinessphonecontactService(BusinessphonecontactService service) {
        this.businessphonecontactService = service;
    }

}

