import com.lsuc.lsuc.Userpreference;


/**
 * ServiceImpl object for domain model class Userpreference.
 *
 * @see Userpreference
 */
@Service("LSUC.UserpreferenceService")
public class UserpreferenceServiceImpl implements UserpreferenceService {

    private static final Logger LOGGER = LoggerFactory.getLogger(UserpreferenceServiceImpl.class);


    @Autowired
    @Qualifier("LSUC.UserpreferenceDao")
    private WMGenericDao<Userpreference, Integer> wmGenericDao;

    public void setWMGenericDao(WMGenericDao<Userpreference, Integer> wmGenericDao) {
        this.wmGenericDao = wmGenericDao;
    }

    @Transactional(value = "LSUCTransactionManager")
    @Override
	public Userpreference create(Userpreference userpreference) {
        LOGGER.debug("Creating a new Userpreference with information: {}", userpreference);
        Userpreference userpreferenceCreated = this.wmGenericDao.create(userpreference);
        return userpreferenceCreated;
    }

	@Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public Userpreference getById(Integer userpreferenceId) throws EntityNotFoundException {
        LOGGER.debug("Finding Userpreference by id: {}", userpreferenceId);
        Userpreference userpreference = this.wmGenericDao.findById(userpreferenceId);
        if (userpreference == null){
            LOGGER.debug("No Userpreference found with id: {}", userpreferenceId);
            throw new EntityNotFoundException(String.valueOf(userpreferenceId));
        }
        return userpreference;
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public Userpreference findById(Integer userpreferenceId) {
        LOGGER.debug("Finding Userpreference by id: {}", userpreferenceId);
        return this.wmGenericDao.findById(userpreferenceId);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Userpreference getByUserFkAndUserPreferenceTypeFk(Integer userFk, Integer userPreferenceTypeFk) {
        Map<String, Object> userFkAndUserPreferenceTypeFkMap = new HashMap<>();
        userFkAndUserPreferenceTypeFkMap.put("userFk", userFk);
        userFkAndUserPreferenceTypeFkMap.put("userPreferenceTypeFk", userPreferenceTypeFk);

        LOGGER.debug("Finding Userpreference by unique keys: {}", userFkAndUserPreferenceTypeFkMap);
        Userpreference userpreference = this.wmGenericDao.findByUniqueKey(userFkAndUserPreferenceTypeFkMap);

        if (userpreference == null){
            LOGGER.debug("No Userpreference found with given unique key values: {}", userFkAndUserPreferenceTypeFkMap);
            throw new EntityNotFoundException(String.valueOf(userFkAndUserPreferenceTypeFkMap));
        }

        return userpreference;
    }

	@Transactional(rollbackFor = EntityNotFoundException.class, value = "LSUCTransactionManager")
	@Override
	public Userpreference update(Userpreference userpreference) throws EntityNotFoundException {
        LOGGER.debug("Updating Userpreference with information: {}", userpreference);
        this.wmGenericDao.update(userpreference);

        Integer userpreferenceId = userpreference.getPk();

        return this.wmGenericDao.findById(userpreferenceId);
    }

    @Transactional(value = "LSUCTransactionManager")
	@Override
	public Userpreference delete(Integer userpreferenceId) throws EntityNotFoundException {
        LOGGER.debug("Deleting Userpreference with id: {}", userpreferenceId);
        Userpreference deleted = this.wmGenericDao.findById(userpreferenceId);
        if (deleted == null) {
            LOGGER.debug("No Userpreference found with id: {}", userpreferenceId);
            throw new EntityNotFoundException(String.valueOf(userpreferenceId));
        }
        this.wmGenericDao.delete(deleted);
        return deleted;
    }

	@Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public Page<Userpreference> findAll(QueryFilter[] queryFilters, Pageable pageable) {
        LOGGER.debug("Finding all Userpreferences");
        return this.wmGenericDao.search(queryFilters, pageable);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Page<Userpreference> findAll(String query, Pageable pageable) {
        LOGGER.debug("Finding all Userpreferences");
        return this.wmGenericDao.searchByQuery(query, pageable);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
    @Override
    public Downloadable export(ExportType exportType, String query, Pageable pageable) {
        LOGGER.debug("exporting data in the service LSUC for table Userpreference to {} format", exportType);
        return this.wmGenericDao.export(exportType, query, pageable);
    }

	@Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
	public long count(String query) {
        return this.wmGenericDao.count(query);
    }

    @Transactional(readOnly = true, value = "LSUCTransactionManager")
	@Override
    public Page<Map<String, Object>> getAggregatedValues(AggregationInfo aggregationInfo, Pageable pageable) {
        return this.wmGenericDao.getAggregatedValues(aggregationInfo, pageable);
    }



}

